async function populateExtended() {
  try {
    const vocab = EXPANDED_VOCABULARIES[language];
    if (!vocab) {
      console.error(`❌ No vocabulary defined for ${language}`);
      console.error('Available languages:', Object.keys(EXPANDED_VOCABULARIES).join(', '));
      process.exit(1);
    }

    console.log('\n' + '═'.repeat(80));
    console.log(`📚 РАСШИРЕННОЕ ЗАПОЛНЕНИЕ СЛОВАРЯ: ${language.toUpperCase()}`);
    console.log('═'.repeat(80) + '\n');

    let totalReplaced = 0;
    let totalAdded = 0;

    for (const [level, categories] of Object.entries(vocab)) {
      console.log(`\n📖 Уровень ${level}:`);
      let levelReplaced = 0;
      let levelAdded = 0;

      for (const [category, words] of Object.entries(categories)) {
        let categoryCount = 0;

        for (const word of words) {
          // Check if word already exists
          const existing = await pool.query(`
            SELECT id FROM source_words_${language}
            WHERE word = $1
          `, [word]);

          if (existing.rows.length > 0) {
            continue;
          }

          // Find a placeholder to replace
          const placeholder = await pool.query(`
            SELECT id FROM source_words_${language}
            WHERE word LIKE '%_word_%' AND level = $1
            LIMIT 1
          `, [level]);

          if (placeholder.rows.length > 0) {
            await pool.query(`
              UPDATE source_words_${language}
              SET word = $1
              WHERE id = $2
            `, [word, placeholder.rows[0].id]);
            categoryCount++;
            levelReplaced++;
            totalReplaced++;
          } else {
            // No placeholders left for this level, insert new
            await pool.query(`
              INSERT INTO source_words_${language} (word, level, theme)
              VALUES ($1, $2, 'general')
            `, [word, level]);
            categoryCount++;
            levelAdded++;
            totalAdded++;
          }
        }

        if (categoryCount > 0) {
          console.log(`   ✅ ${category}: ${categoryCount} слов`);
        }
      }

      console.log(`   📊 Итого ${level}: ${levelReplaced} заменено, ${levelAdded} добавлено`);
    }

    // Check remaining placeholders
    const remaining = await pool.query(`
      SELECT COUNT(*) as count FROM source_words_${language}
      WHERE word LIKE '%_word_%'
    `);

    const realWords = await pool.query(`
      SELECT COUNT(*) as count FROM source_words_${language}
      WHERE word NOT LIKE '%_word_%'
    `);

    console.log('\n' + '─'.repeat(80));
    console.log(`✅ Итого: ${totalReplaced} плейсхолдеров заменено, ${totalAdded} новых слов добавлено`);
    console.log(`📈 Реальных слов в базе: ${realWords.rows[0].count}`);
    console.log(`📉 Осталось плейсхолдеров: ${remaining.rows[0].count}`);

    const total = parseInt(realWords.rows[0].count) + parseInt(remaining.rows[0].count);
    const percentage = ((parseInt(realWords.rows[0].count) / total) * 100).toFixed(1);
    console.log(`📊 Процент реальных слов: ${percentage}%`);
    console.log('═'.repeat(80) + '\n');

  } catch (error) {
    console.error('❌ Error:', error.message);
    console.error(error.stack);
  } finally {
    await pool.end();
  }
}

populateExtended();
